// Renamed ProtectedRoute → AuthGuard for a clearer purpose.
// Replaced isAuthenticated with authToken for a more practical authentication check.
// Refactored conditional logic:
//     Instead of using an if statement, it now directly returns the appropriate component.
// Updated route redirection:
//     "sign-in" → "login" (More commonly used in authentication flows).
// Removed unnecessary comments and improved readability.


import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';

const AuthGuard = () => {
  const authToken = localStorage.getItem('authToken');

  // Redirect to login if user is not authenticated
  return authToken ? <Outlet /> : <Navigate to="/login" replace />;
};

export default AuthGuard;
