import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getUserProfile } from '../services/api';

const ProfilePage = ({ userId }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check authentication
    const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
    if (!isAuthenticated) {
      navigate('/sign-in');
      return;
    }

    const fetchUserProfile = async () => {
      try {
        setLoading(true);
        const userData = await getUserProfile(userId);
        setUser(userData);
        setError(null);
      } catch (err) {
        setError('Failed to load user profile. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [userId, navigate]);

  if (loading) {
    return <div className="card">Loading profile information...</div>;
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>;
  }

  if (!user) {
    return <div className="alert alert-danger">User not found</div>;
  }

  return (
    <div>
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">User Profile</h2>
          <Link to="/edit-profile">
            <button>Edit Profile</button>
          </Link>
        </div>
        
        <div className="profile-info">
          <div className="form-group">
            <label>Name</label>
            <p>{user.name}</p>
          </div>
          
          <div className="form-group">
            <label>Email</label>
            <p>{user.email}</p>
          </div>
          
          <div className="form-group">
            <label>Role</label>
            <p>{user.role}</p>
          </div>
          
          <div className="form-group">
            <label>Profile Visibility</label>
            <p>{user.profileVisibility ? 'Public' : 'Private'}</p>
          </div>
          
          <div className="form-group">
            <label>Notifications</label>
            <p>{user.notificationPreference ? 'Enabled' : 'Disabled'}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
