// Renamed Navbar → NavigationBar for a more distinct name.
// Renamed variables and functions for better clarity:

//     handleSignOut → logoutUser
//     isAuthenticated → authToken
//     userId → currentUser
//     navigate → redirect
//     location → currentPath

// Updated navigation labels:

//     "Profile" → "Dashboard"
//     "Settings" → "Preferences"
//     "Sign Out" → "Logout"

// Improved CSS class names for better styling management:

//     "navbar" → "navigation"
//     "navbar-container" → "nav-wrapper"
//     "navbar-logo" → "brand-logo"
//     "navbar-menu" → "nav-links"
//     "navbar-item" → "nav-item"
//     "sign-out-button" → "logout-btn"

// Refactored authentication logic to remove isAuthenticated and use a more practical authToken.

import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';

const NavigationBar = () => {
  const currentPath = useLocation().pathname;
  const redirect = useNavigate();

  const logoutUser = () => {
    // Clear authentication details
    localStorage.removeItem('authToken');
    localStorage.removeItem('currentUser');

    // Redirect to login page
    redirect('/login');
  };

  return (
    <nav className="navigation">
      <div className="nav-wrapper">
        <div className="brand-logo">
          <Link to="/">User Hub</Link>
        </div>
        <ul className="nav-links">
          <li className="nav-item">
            <Link to="/" className={currentPath === '/' ? 'active-link' : ''}>
              Dashboard
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/preferences" className={currentPath === '/preferences' ? 'active-link' : ''}>
              Preferences
            </Link>
          </li>
          <li className="nav-item">
            <button onClick={logoutUser} className="logout-btn">
              Logout
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default NavigationBar;
