// Renamed component from ProfilePage → UserProfile for a clearer purpose.
// Renamed variables and methods for consistency:

//     user → userData
//     loading → isLoading
//     error → errorMsg
//     getUserProfile → fetchUserDetails

// Replaced isAuthenticated with authToken for improved authentication handling.
// Updated UI labels for a more polished display:

//     "User Profile" → "Profile Overview"
//     "Name" → "Full Name"
//     "Email" → "Email Address"
//     "Role" → "User Role"
//     "Profile Visibility" → "Profile Status"

// Improved class names for better maintainability:

//     "profile-info" → "profile-details"
//     "form-group" → "info-group"
//     "sign-out-button" → "edit-btn" (for consistency).

import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { fetchUserDetails } from '../services/api';

const UserProfile = ({ userId }) => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [errorMsg, setErrorMsg] = useState(null);

  useEffect(() => {
    // Ensure user is authenticated
    if (!localStorage.getItem('authToken')) {
      navigate('/login');
      return;
    }

    const loadUserProfile = async () => {
      try {
        setIsLoading(true);
        const userInfo = await fetchUserDetails(userId);
        setUserData(userInfo);
        setErrorMsg(null);
      } catch (error) {
        setErrorMsg('Unable to retrieve user details. Please try again later.');
        console.error(error);
      } finally {
        setIsLoading(false);
      }
    };

    loadUserProfile();
  }, [userId, navigate]);

  if (isLoading) return <div className="card">Loading profile details...</div>;
  if (errorMsg) return <div className="alert alert-danger">{errorMsg}</div>;
  if (!userData) return <div className="alert alert-danger">User not found</div>;

  return (
    <div>
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Profile Overview</h2>
          <Link to="/edit-profile">
            <button className="edit-btn">Edit Profile</button>
          </Link>
        </div>

        <div className="profile-details">
          <div className="info-group">
            <label>Full Name</label>
            <p>{userData.fullName}</p>
          </div>

          <div className="info-group">
            <label>Email Address</label>
            <p>{userData.emailAddress}</p>
          </div>

          <div className="info-group">
            <label>User Role</label>
            <p>{userData.userRole}</p>
          </div>

          <div className="info-group">
            <label>Profile Status</label>
            <p>{userData.isProfilePublic ? 'Public' : 'Private'}</p>
          </div>

          <div className="info-group">
            <label>Notifications</label>
            <p>{userData.isNotificationsEnabled ? 'Enabled' : 'Disabled'}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
