import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

// Create axios instance with base URL
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// User profile API calls
export const getUserProfile = async (userId) => {
  try {
    const response = await api.get(`/users/${userId}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching user profile:', error);
    throw error;
  }
};

export const updateUserProfile = async (userId, userData) => {
  try {
    const response = await api.put(`/users/${userId}`, userData);
    return response.data;
  } catch (error) {
    console.error('Error updating user profile:', error);
    throw error;
  }
};

// User settings API calls
export const updateProfileVisibility = async (userId, isPublic) => {
  try {
    const response = await api.patch(`/users/${userId}/visibility`, { isPublic });
    return response.data;
  } catch (error) {
    console.error('Error updating profile visibility:', error);
    throw error;
  }
};

export const updateNotificationPreference = async (userId, isEnabled) => {
  try {
    const response = await api.patch(`/users/${userId}/notifications`, { isEnabled });
    return response.data;
  } catch (error) {
    console.error('Error updating notification preference:', error);
    throw error;
  }
};

export default {
  getUserProfile,
  updateUserProfile,
  updateProfileVisibility,
  updateNotificationPreference,
};
