import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUserProfile, updateProfileVisibility, updateNotificationPreference } from '../services/api';
import './SettingsPage.css';

const SettingsPage = ({ userId }) => {
  const navigate = useNavigate();
  const [settings, setSettings] = useState({
    profileVisibility: false,
    notificationPreference: false,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  useEffect(() => {
    // Check authentication
    const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
    if (!isAuthenticated) {
      navigate('/sign-in');
      return;
    }

    const fetchUserSettings = async () => {
      try {
        setLoading(true);
        const userData = await getUserProfile(userId);
        setSettings({
          profileVisibility: userData.profileVisibility,
          notificationPreference: userData.notificationPreference,
        });
        setError(null);
      } catch (err) {
        setError('Failed to load user settings. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserSettings();
  }, [userId, navigate]);

  const handleVisibilityChange = async (e) => {
    const isPublic = e.target.checked;
    try {
      setError(null);
      setSuccessMessage(null);
      
      await updateProfileVisibility(userId, isPublic);
      setSettings(prevSettings => ({
        ...prevSettings,
        profileVisibility: isPublic
      }));
      
      setSuccessMessage('Profile visibility updated successfully!');
      setTimeout(() => setSuccessMessage(null), 3000);
    } catch (err) {
      setError('Failed to update profile visibility. Please try again.');
      console.error(err);
    }
  };

  const handleNotificationChange = async (e) => {
    const isEnabled = e.target.checked;
    try {
      setError(null);
      setSuccessMessage(null);
      
      await updateNotificationPreference(userId, isEnabled);
      setSettings(prevSettings => ({
        ...prevSettings,
        notificationPreference: isEnabled
      }));
      
      setSuccessMessage('Notification preference updated successfully!');
      setTimeout(() => setSuccessMessage(null), 3000);
    } catch (err) {
      setError('Failed to update notification preference. Please try again.');
      console.error(err);
    }
  };

  if (loading) {
    return <div className="card">Loading settings...</div>;
  }

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="card-title">User Settings</h2>
      </div>
      
      {error && <div className="alert alert-danger">{error}</div>}
      {successMessage && <div className="alert alert-success">{successMessage}</div>}
      
      <div className="settings-container">
        <div className="setting-item">
          <div className="setting-info">
            <h3>Profile Visibility</h3>
            <p>Make your profile visible to other researchers in the system.</p>
          </div>
          <label className="toggle-switch">
            <input
              type="checkbox"
              checked={settings.profileVisibility}
              onChange={handleVisibilityChange}
            />
            <span className="slider"></span>
          </label>
          <div className="setting-status">
            {settings.profileVisibility ? 'Public' : 'Private'}
          </div>
        </div>
        
        <div className="setting-item">
          <div className="setting-info">
            <h3>Notifications</h3>
            <p>Receive notifications about system updates and research opportunities.</p>
          </div>
          <label className="toggle-switch">
            <input
              type="checkbox"
              checked={settings.notificationPreference}
              onChange={handleNotificationChange}
            />
            <span className="slider"></span>
          </label>
          <div className="setting-status">
            {settings.notificationPreference ? 'Enabled' : 'Disabled'}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SettingsPage;
